    state : {type : Number, required : false, default:1 }, //1 es activado
    products: [{type: Object}],
    categories :[ { type : Object }]
}, {
    timestamps: true
})

const Cupone  = mongoose.model("cupones", CuponeSchema);
export default Cupone;

