                    message: "LA DIRECCIÓN DEL CLIENTE SE REGISTRO CORRECTAMENTE",
                    address_client: address_client,
                })
        } catch (error) {
            console.log(error)
            res.status(500).send({ message: "OCURRIÓ UN PROBLEMA" });
        }
